.media-object {
    --border-width: 1px;
    --radius: 24px;
    height: 100%;
    position: relative;

    border-radius: var(--radius);
    border: var(--border-width) solid transparent;
}

.media-object::before {
    content: " ";
    position: absolute;
    inset: calc(var(--border-width) * -1);
    z-index: -1;
    border: inherit;
    border-radius: inherit;
    background-image: conic-gradient(from var(--angle),
            #381d6a 80%,
            #e0d1ff 88%,
            #e0d1ff 92%,
            #381d6a 100%);
    background-origin: border-box;
    -webkit-mask: linear-gradient(black, black) content-box,
        linear-gradient(black, black);
    mask: linear-gradient(black, black), linear-gradient(black, black);
    -webkit-mask-clip: content-box, border-box;
    mask-clip: content-box, border-box;
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    animation: spin 3s linear infinite;
}

@supports not (background: paint(something)) {
    .media-object::before {
        background-image: conic-gradient(#381d6a 80%,
                #e0d1ff 88%,
                #e0d1ff 92%,
                #381d6a 100%);
    }
}

.media-object:hover::before {
    animation-play-state: paused;
}

@property --angle {
    syntax: "<angle>";
    inherits: true;
    initial-value: 0turn;
}

@keyframes spin {
    to {
        --angle: 1turn;
    }
}

body {
    margin: 0;
    min-height: 100vh;
    display: grid;
    place-items: center;
}

html {
    box-sizing: border-box;
    scroll-behavior: smooth;
}

@media (prefers-reduced-motion) {
    html {
        scroll-behavior: none;
    }
}

html *,
html *::after,
html *::before {
    box-sizing: inherit;
}



.card {
    --text-color: #fff;
    --text-color-2: #f3f8fa;
    --bg-color: #0f0620;
    --border-color: #381d6a;

    display: flex;
    flex-direction: column;
    gap: 24px;
    width: 100%;
    padding: 32px;
    height: 100%;
    border-radius: 24px;
    color: var(--text-color);
    background: var(--bg-color);
    border: 1px solid var(--border-color);
    font: 100%/1.6 "Open Sans", sans-serif;
}

.card__title {
    margin-block: 0;
    font-family: "Sora", sans-serif;
    font-size: 33px;
    font-weight: 700;
    line-height: 44px;
}

mark {
    background: linear-gradient(270deg, #d42f4a 0%, #f82bbf 100%);
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: #0000;
    -webkit-box-decoration-break: clone;
}

.card__description {
    color: var(--text-color-2);
    line-height: 24px;
    margin-block: 0;
}

.card__actions {
    display: flex;
    gap: 24px;
}

.card__button {
    display: block;
    padding: 16px 32px;
    border: 1px solid var(--border-color);
    border-radius: 24px;
    text-decoration: none;
    color: inherit;
    flex-grow: 1;
    text-align: center;
    font-weight: 600;
    line-height: 16px;
}

.media-object {
    display: flex;
    justify-content: space-between;
    align-items: end;
    padding: 24px;
    gap: 24px;
}

.media-object__title {
    font-size: 18px;
    font-weight: 600;
    line-height: 27px;
    margin: 0 0 16px;
}

.media-object__button {
    display: flex;
    align-items: center;
    width: fit-content;
    gap: 8px;
    padding: 16px 38px 14px;
    border-radius: 28px;
    background: linear-gradient(270deg, #e8488a 0%, #d5304b 100%);
    color: #fff;
    text-decoration: none;
    font-family: "Sora", sans-serif;
    font-weight: 600;
    line-height: 16px;
    text-align: center;
}

