.source-link {
    position: fixed;
    right: 32px;
    bottom: 32px;
    width: fit-content;
    box-sizing: border-box;
    font-size: 16px;
    display: flex;
    padding: 0.75em 1em 0.75em 0.75em;
    justify-content: center;
    align-items: center;
    gap: 0.5em;
    border-radius: 128px;
    background: linear-gradient(270deg, #afe8ef 0%, #e7e1fa 100%);
    color: #000;
    text-align: center;
    font-style: normal;
    font-weight: 400;
    line-height: 150%;
    text-decoration: none;
}

.source-link svg {
    display: block;
    width: 1.5em;
    height: 1.5em;
    border-radius: 0.25em;
}

.source-link.source-link--second {
    right: auto;
    left: 32px;
}

.box-header-info {
    width: 85%;
    position: relative;
    height: 300px;

    margin-left: 7.5%;
    top: 100px;
    border-radius: 50px;
}

.box-bg {
    position: absolute;
    z-index: -1;
    width: 95%;
    margin-left: 2.5%;
    height: 300px;
    border-bottom-left-radius: 30px;
    border-bottom-right-radius: 30px;
    background: linear-gradient(90deg, rgba(9, 26, 85, 1) 0%, rgba(39, 70, 180, 1) 100%);
}

.headercontainer {
    width: 100%;
    height: 400px;

    overflow: hidden;

}

.navigatorcontent {
    width: 100%;
    height: 400px;


}

.navigator-info-container {
    width: 1000px;
    height: 80%;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;

}

#navigator-img {
    width: 100%;
    height: 400px;
    position: absolute;
    z-index: -1;
}

.titlenavigator p {
    font-size: 80px;
    color: #fff;
    font-family: notosanlao-bold;
    padding-bottom: 70px;
}

.navigatorlink p {
    color: #fff;
    font-size: 20px;

    font-family: notosanlao;
}
